mapper.insert:
select REVIEW_NO.nextval from dual
INSERT INTO REVIEW VALUES( #{reviewNo}, #{subject}, #{content}, #{pictureName}, #{thumbnailName}, #{star}, SYSDATE, #{memberId}, #{outerNo}
)

mapper.select:
	
		SELECT 
		FROM(
			SELECT ROWNUM RNUM, 
			FROM( 			
						
                        ORDER BY REVIEW_NO DESC 
				)
			WHERE ROWNUM <=#{endIndex}	
			)
		WHERE RNUM>=#{startIndex}

		SELECT 
		FROM(
			SELECT ROWNUM RNUM, 
			FROM( 			
						
						WHERE MEMBER_ID = #{memberId}
                        ORDER BY REVIEW_NO DESC 
				)
			WHERE ROWNUM <=#{endIndex}	
			)
		WHERE RNUM>=#{startIndex}
